create table t_issues
(
    id          int auto_increment
        primary key,
    repo_id     int                      not null comment 'The id of the repo contains this issue',
    issue_url   varchar(500)  default '' null comment 'The url of issues',
    fix_url     varchar(500)  default '' null comment 'The url of the commit of fix',
    update_time datetime(6)              null comment 'The update time of this record',
    create_time datetime(6)              null comment 'The create time of this record',
    description varchar(1000) default '' null comment 'The description of this issues',
    fix         varchar(1000) default '' null comment 'The description of how to fix this issues',
    constraint fk_repo_id
        foreign key (repo_id) references t_repos (id)
);

