create table t_repos
(
    id                   int auto_increment comment 'repo_id'
        primary key,
    repo_name            varchar(256) default 'repo_name'  not null,
    github_url           varchar(256) default 'github_url' not null,
    owner                varchar(128) default 'owner'      not null comment 'owner of repo',
    name                 varchar(128) default 'name'       not null comment 'name of repo',
    is_searched          int          default 0            not null comment '0 stands for searched repos of input topics',
    is_filtered_keywords int          default 0            null comment '1 filter out, 0 means that repo not related to the keywords about leetcode/algorithm',
    has_downloaded       int          default 0            not null comment '0 undownloaded,1 downloaded',
    is_contained_depen   int          default 0            not null comment '0 not contain aimed dependencies 1 contained',
    constraint index1
        unique (id)
);

